void init_gfx(void) {
    // Load Background tiles and then map
    set_bkg_data(0, 95u, Terminal);
    //set_bkg_tiles(0, 0, 32u, 32u, dungeon_mapPLN0);
	set_bkg_tiles(0,0,Tilemap1Width,Tilemap1Height,Tilemap1);			
	// Turn the background map on to make it visible
    SHOW_BKG;
}


void main(void)
{
	init_gfx();

    int cursor = 0;
    // Loop forever
    while(1) {


		// Game main loop processing goes here
        if(joypad() != 0)
        {
            Tilemap1[cursor] = joypad() - 1;
            cursor++;
        }

        //Update Tiles
	    set_bkg_tiles(0,0,Tilemap1Width,Tilemap1Height,Tilemap1);			

		// Done processing, yield CPU and wait for start of next frame
        vsync();
    }
}
